package com.ly.common.utils.rest;import java.io.Serializable;import java.util.List;/** * Created by liyu2 on 2017/10/11. */public class Pagination<T> implements Serializable {    private int pageNum;    private int pageSize;    private long total;    private List<T> list;    public Pagination(List<T> list, int pageNum, int pageSize, long total)    {        this.pageNum = pageNum;        this.pageSize = pageSize;        this.total = total;        this.list = list;    }    public int getPageNum() {        return this.pageNum;    }    public void setPageNum(int pageNum) {        this.pageNum = pageNum;    }    public int getPageSize() {        return this.pageSize;    }    public void setPageSize(int pageSize) {        this.pageSize = pageSize;    }    public long getTotal() {        return this.total;    }    public void setTotal(long total) {        this.total = total;    }    public List<T> getList() {        return this.list;    }    public void setList(List<T> list) {        this.list = list;    }    public String toString()    {        return "{pageNum:" + this.pageNum + ", pageSize:" + this.pageSize + ", total:" + this.total + ", list:" + this.list + '}';    }}