package com.ly.common.utils.rest;/** * Created by liyu2 on 2017/10/11. */public class Result {    public static Integer RECODE_SUCCESS = Integer.valueOf(1);    public static Integer RECODE_ERROR = Integer.valueOf(0);    public static Integer RECODE_UNLOGIN = Integer.valueOf(-1);    public static Integer RECODE_VALIDATE_ERROR = Integer.valueOf(-2);    private String errMsg;    private Integer retCode;    private Object data;    public String getErrMsg()    {        return this.errMsg;    }    public void setErrMsg(String errMsg) {        this.errMsg = errMsg;    }    public Integer getRetCode() {        return this.retCode;    }    public void setRetCode(Integer retCode) {        this.retCode = retCode;    }    public Object getData() {        return this.data;    }    public void setData(Object data) {        this.data = data;    }    public String toString()    {        return " {\"errMsg\":" + this.errMsg + ", \"retCode\":" + this.retCode + ", \"data\":" + this.data + "}";    }}